const bcc = Inputs.BCC;
const from = Inputs.From;
const subject = Inputs.Subject;
const apiKey = Inputs.APIKey;

if(apiKey === undefined || apiKey === "") {
    Outputs.Failure()
    throw Error("No API Key provided!")
}

const personalizations = [
    { "to": (Array.isArray(to)?to.map(e => ({email:e}) ):[{"email": to}]) }
]

if(cc !== undefined) {
    personalizations[0]["cc"] = (Array.isArray(cc)?cc.map(e => ({email:e}) ):[{"email": cc}])
}

if(bcc !== undefined) {
    personalizations[0]["bcc"] = (Array.isArray(bcc)?bcc.map(e => ({email:e}) ):[{"email": bcc}]) 

}

const res = await fetch("https://api.sendgrid.com/v3/mail/send",{
    method:'POST',
    headers:{
        "Authorization":"Bearer " + apiKey,
        "Content-Type":"application/json"
    },
    body:JSON.stringify({
    personalizations,
    "from": {"email":from},
    "subject": subject,
    "content": (html!==undefined)?
     [{"type": "text/html", "value": html}]:
    [{"type": "text/plain", "value": text}]

    })
})

if(res.ok) {
    Outputs.Success()
