console.log('Script started at', new Date().toISOString());
console.log('Input fileUrl:', Inputs.fileUrl);

const fileUrl = Inputs.fileUrl;

if (!fileUrl || typeof fileUrl !== 'string') {
  console.error('fileUrl is invalid or not set:', fileUrl);
  Outputs.errorMessage = 'Failed to upload file. Please try again.';
  return;
}

console.log('Fetching file from:', fileUrl);

if (typeof XLSX === 'undefined') {
  console.error('XLSX library not loaded. Check the external link.');
  Outputs.errorMessage = 'XLSX library not loaded. Please try again.';
  return;
}

fetch(fileUrl)
  .then(response => {
    if (!response.ok) {
      throw new Error('Network response failed: ' + response.statusText);
    }
    return response.arrayBuffer();
  })
  .then(data => {
    const workbook = XLSX.read(data, { type: 'array' });
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];
    const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
    console.log('Parsed Excel Data:', JSON.stringify(jsonData, null, 2));

    const headers = jsonData[0].map(header => header.trim());
    const dataRows = jsonData.slice(1);

    let savedCount = 0;
    dataRows.forEach((row, index) => {
      console.log(`Processing row ${index + 2}:`, row);
      const rowData = {
        updatedAt: new Date().toISOString(),
        segment: row[headers.indexOf('segment')] || 'undefined',
        country: row[headers.indexOf('country')] || 'undefined',
        product: row[headers.indexOf('product')] || 'undefined',
        discount: row[headers.indexOf('discount')] || 'undefined',
        unit: String(row[headers.indexOf('unit')] || '')
      };
      console.log('Creating record:', rowData);
      try {
        Outputs.object = rowData;
        savedCount++;
        Outputs.success();
        console.log(`Successfully saved row ${index + 2}`);
      } catch (error) {
        console.error(`Failed to save row ${index + 2}:`, error);
      }
    });

    console.log(`Total rows processed: ${dataRows.length}, Successfully saved: ${savedCount}`);
    Outputs.errorMessage = '';

    const gridContainer = document.getElementById('gridContainer');
    if (gridContainer) {
      const headersHtml = headers.map(cell => `<th>${cell}</th>`).join('');
      const rowsHtml = dataRows.map(row => `<tr>${row.map(cell => `<td>${cell}</td>`).join('')}</tr>`).join('');
      gridContainer.innerHTML = `<table border="1"><thead><tr>${headersHtml}</tr></thead><tbody>${rowsHtml}</tbody></table>`;
    }
  })
  .catch(error => {
    console.error('Error processing Excel file:', error);
    Outputs.errorMessage = 'Error processing Excel file: ' + error.message;
  });

