// Format a date object into a string
function formatDate(date) {
    if(!(date instanceof Date)) return

    const months = [
        'Jan',
        'Feb',
        'Mar',
        'Apr',
        'May',
        'Jun',
        'Jul',
        'Aug',
        'Sep',
        'Oct',
        'Nov',
        'Dec'
    ]

    let newDateString = months[date.getMonth()] + ' ' +date.getDate() + ', ' + date.getFullYear()

    return newDateString
}

// Parse a date string in the format we use into a date object
// if possible
function parseDate(_dateString) {
    // No valid date, return todays date
    if(_dateString === undefined || _dateString.length < 3) 
        return null

    let dateString = _dateString.trim().toLowerCase()

    const months = {
        'jan':0,
        'feb':1,
        'mar':2,
        'apr':3,
        'may':4,
        'jun':5,
        'jul':6,
        'aug':7,
        'sep':8,
        'oct':9,
        'nov':10,
        'dec':11
    }

    let month = months[dateString.substring(0,3)]
    if(month === undefined) return new Date() // Not a valid month

    // Remove month
    dateString = dateString.substring(3)
    dateString = dateString.trim()

    let sepIndex = dateString.indexOf(',')
    let dayString = dateString.substring(0,sepIndex==-1?dateString.length:sepIndex)

    let day = parseInt(dayString) || 1

    let year = (new Date()).getFullYear()
    if(sepIndex !== -1) {
        dateString = dateString.substring(sepIndex+1)
        dateString = dateString.trim()

        if(dateString.length === 4)
        year = parseInt(dateString)
    }

    return new Date(year,month,day)
}

// Compare two dates, ignoring the time
function compareDates(a,b) {
    if(a.getFullYear() === b.getFullYear() && a.getDate() === b.getDate() && a.getMonth() === b.getMonth())
        return true

    return false
}

